#define FNV_OFFSET_BASIS_32 2166136261u
#define FNV_PRIME_32 16777619u

uint32_t fnv1a_32(const char *data, size_t length) {
    uint32_t hash = FNV_OFFSET_BASIS_32;
    for (size_t i = 0; i &lt; length; ++i) {
        hash ^= (uint32_t)data[i];
        hash *= FNV_PRIME_32;
    }
    return hash;
}
</code></pre> <pre><code>inline uint32_t fnv1a_32(std::string const &amp; text) {
    // 32 bit params
    uint32_t constexpr fnv_prime = 16777619U;
    uint32_t constexpr fnv_offset_basis = 2166136261U;

    uint32_t hash = fnv_offset_basis;

    for(auto c: text) {
        hash ^= c;
        hash *= fnv_prime;
    }

    return hash;
}
